echo ---- Deploy $2 to Production Machine on $1----
ssh root@$1 "sudo service docker stop ; sudo pkill -9 docker; docker rm -f $(docker ps -aq); docker rmi -f $(docker images -q); sudo service docker start ; docker pull hardag/tictactoe:"$2" && docker run -p 80:8080 -d -e "NODE_ENV=production" hardag/tictactoe:"$2" && echo ---- Docker running on $1 ----"